css:
@charset "UTF-8";
body {
  margin: 0;
  padding: 0;
  background-color: #085FAF;
}

.wrap h1, h2, ul, p, th, td {
  margin: 0;
  padding: 0;
}

.wrap {
  display: block;
  position: relative;
  padding: 30px 0;
  background-color: #085FAF;
}
.wrap ul {
  display: flex;
  list-style-type: none;
  text-align: center;
  margin: 0px 10px 0px 30px;
  color: #fff;
}
.wrap li {
  display: block;
  width: 100%;
  margin-right: 20px;
  border-top: 1px solid #0c96db;
  border-left: 1px solid #0c96db;
  border-right: 1px solid #0c96db;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  background-color: #002977;
  transform: translateY(1px);
}
.wrap li:hover {
  background-color: #064b96;
}

.container {
  margin: 0 auto;
  max-width: 1500px;
  width: 100%;
}

.panel_label {
  display: block;
  width: 100%;
  padding: 10px 5px;
  position: relative;
  color: #fff;
  font-size: 1.2rem;
  transform: translateY(1px);
}

.panel_area {
  display: block;
  position: relative;
  margin: 0 30px 30px 30px;
  border: 1px solid #0c96db;
  background: linear-gradient(to bottom, #064b96, #002977);
  background-color: #064b96;
  border-bottom-left-radius: 5px;
  border-bottom-right-radius: 5px;
}

.panel_contents {
  color: #fff;
  font-size: 2rem;
}

/*Delete all radio buttons*/
input[name="panel_label"] {
  display: none;
}

/*Style for tabs*/
.panel_contents {
  display: none;
  padding: 40px;
  clear: both;
  overflow: hidden;
}

/*Show only the contents of selected tab*/
#panel_1:checked ~ .panel_area #tab1, #panel_2:checked ~ .panel_area #tab2, #panel_3:checked ~ .panel_area #tab3 {
  display: block;
}

/*Change style for selected tab*/
#panel_1:checked ~ ul .panel_list1, #panel_2:checked ~ ul .panel_list2, #panel_3:checked ~ ul .panel_list3 {
  background-color: #064b96;
  color: #fff;
  z-index: 2;
  height: 100%;
}

.block {
  display: flex;
  justify-content: space-between;
}
.block img {
  margin: 0 auto;
}

.stock {
  border-bottom: 1px solid #fff;
  padding-bottom: 20px;
}

.left_box h1, .right_box h1 {
  font-size: 2rem;
  font-weight: bold;
}

.left_box h2 {
  font-size: 1rem;
  font-weight: normal;
}
.left_box p {
  font-size: 1.5rem;
  margin-top: 10px;
}

.right_box {
  /*display: flex;*/
}

.budgettable {
  display: flex;
}

.text_size {
  font-size: 4rem;
  font-weight: bold;
}

.text_color {
  margin-left: 20px;
}

table {
  color: #fff;
  font-size: 0.7rem;
  width: 250px;
  margin-left: 80px;
  border-collapse: collapse;
}
table th {
  text-align: left;
  font-weight: normal;
  border-bottom: solid 1px #0c96db;
  padding-top: 5px;
}
table td {
  text-align: right;
  font-weight: bold;
  border-bottom: solid 1px #0c96db;
  padding-top: 5px;
}
table:first-child {
  margin-left: 0;
}

@media (max-width: 1300px) {
  table {
    margin-left: 30px;
  }

  .text_size {
    font-size: 3rem;
  }

  .left_box p {
    font-size: 1.4rem;
  }

  .text_color {
    margin-left: 0;
    display: block;
  }
}
@media (max-width: 1000px) {
  .wrap {
    width: 100vw;
  }

  .panel_contents {
    padding: 20px;
  }

  .block {
    flex-direction: column;
  }
  .block img {
    width: 100%;
  }

  table {
    margin-left: 0;
    width: 100%;
  }
  table:first-child {
    margin-right: 15px;
  }
  table:last-child {
    margin-left: 15px;
  }
}
@media (max-width: 576px) {
  .wrap ul {
    margin: 0px 30px;
  }
  .wrap li {
    margin-right: 0;
  }

  .panel_label {
    font-size: 16px;
  }
}

