css:
.designportal-classic dd, .designportal-classic dl, .designportal-classic dt, .designportal-classic p, .designportal-classic td, .designportal-classic th {
  margin: 0;
  padding: 0;
}

.designportal-classic h1, .designportal-classic h2, .designportal-classic h3, .designportal-classic h4, .designportal-classic h5, .designportal-classic h6 {
  margin: 0.8rem 0 0 0;
  padding: 0;
}

.designportal-classic ul, .designportal-classic ol {
  padding-left: 1.8em;
  margin: 0;
}

.designportal {
  font-size: 1em;
  padding: 1em;
}

.designportal .classic-option_flex {
  display: flex;
}

.designportal .classic-option_flex-1 {
  flex: 1;
  position: relative;
}

.designportal .classic-option_flex-2 {
  flex: 2;
  position: relative;
}

.designportal .classic-option_flex-3 {
  flex: 3;
  position: relative;
}

.designportal .classic-option_flex-4 {
  flex: 4;
  position: relative;
}

.designportal .classic-option_flex-5 {
  flex: 5;
  position: relative;
}

.designportal .classic_spacer {
  padding: 1.5em;
}

.designportal .classic_spacer :first-child {
  margin-top: 0;
}

.designportal .classic-heading2 {
  font-weight: 400;
  font-size: 1.6em;
}

.designportal .classic-heading3 {
  font-weight: 400;
  font-size: 1.4em;
}

.designportal .classic-container {
  letter-spacing: 0.04em;
}

.designportal .classic-contents {
  margin-left: 1em;
}

.designportal .classic-contents:first-child {
  margin: 0;
}

.designportal .classic-contents_wrap {
  margin-bottom: 1em;
  width: auto;
  background-color: #fff;
  letter-spacing: 0.04em;
}

.designportal .classic-contents_header {
  border-bottom: 1px solid #e3e7e8;
  display: flex;
  align-items: center;
  margin: 0;
  font-size: 1.25em;
  font-weight: 400;
}

.designportal .classic-contents_title {
  display: inline;
  margin-left: 1.25em;
}

.designportal .classic-list {
  list-style-type: none;
  margin: 0;
  padding: 0;
  font-size: 1em;
}

.designportal .classic-list_item {
  position: relative;
  word-wrap: normal;
  height: 3.5em;
  padding: 0 1em;
  display: flex;
  align-items: center;
}

.designportal .classic-list_item:nth-of-type(2n+1) {
  background-color: #fff;
}

.designportal .classic-list_item:nth-of-type(2n) {
  background-color: #f2f9fe;
}

.designportal .classic-list_icon {
  display: flex;
  align-items: center;
  justify-content: center;
  align-items: center;
  width: 2.5em;
  height: 2.5em;
  fill: #fff;
}

.designportal .classic-list_icon svg {
  width: 2em;
  height: 2em;
}

.designportal .classic-list_name {
  padding-left: 0.5em;
}

.designportal .classic-colour--blue {
  background: linear-gradient(180deg, #56CCF2 0%, #2F80ED 100%);
}

.designportal .classic-colour--pink {
  background: linear-gradient(180deg, #FF6EA2 0%, #E71F00 100%);
}

.designportal .classic-colour--green {
  background: linear-gradient(180deg, #6FCF97 0%, #219653 100%);
}

.designportal .classic-colour--purple {
  background: linear-gradient(180deg, #D16EFF 0%, #5300BD 100%);
}

.designportal .classic-colour--orange {
  background: linear-gradient(180deg, #F2C94C 0%, #EB5757 100%);
}

@media screen and (max-width: 768px) {
  .designportal {
    font-size: 1.6rem;
  }
  .designportal .classic-option_flex {
    display: block;
  }
  .designportal .classic-contents {
    margin: 0;
  }
}
